import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';

import Helmet from '../components/Helmet';
import Section, { SectionBody, SectionTitle } from '../components/Section';
import Grid from '../components/Grid';
import ProductCard from '../components/ProductCard';
import ProductView from '../components/ProductView';
import heroSliderData from '../assets/fake-data/hero-'; // Assuming this is where your product data is

const Product = () => {
    const { slug } = useParams();

    // Find the product by slug
    const product = heroSliderData.find(item => item.slug === slug);

    // Related products (you can implement a more sophisticated logic to find related products)
    const relatedProducts = heroSliderData.filter(item => item.category === product?.category);

    // Scroll to top when product changes
    useEffect(() => {
        if (product) {
            window.scrollTo(0, 0);
        }
    }, [product]);

    if (!product) {
        return (
            <div className="error-page">
                <h2>Product Not Found</h2>
                <p>The product you are looking for does not exist.</p>
            </div>
        );
    }

    return (
        <Helmet title={product.title}>
            <Section>
                <SectionBody>
                    <ProductView product={product} />
                </SectionBody>
            </Section>
            <Section>
                <SectionTitle>Explore More</SectionTitle>
                <SectionBody>
                    <Grid col={4} mdCol={2} smCol={1} gap={20}>
                        {relatedProducts.map((item, index) => (
                            <ProductCard
                                key={index}
                                img01={item.image01}
                                img02={item.image02}
                                name={item.title}
                                price={Number(item.price)}
                                slug={item.slug}
                            />
                        ))}
                    </Grid>
                </SectionBody>
            </Section>
        </Helmet>
    );
};

export default Product;
