const img1 = require("../images/slider/slide_1.png").default;
const img2 = require("../images/slider/slide_2.png").default;
const img3 = require("../images/slider/slide_3.png").default;

const heroSliderData = [
    {
        title: "Premium Pima Women's Polo",
        description: "When it comes to luxury, the pima fabric is a must-mention. It provides the best quality for any fashion product. Pima yarn is twice as long and thick as regular cotton yarn due to advanced weaving technology. This makes the polo shirt more durable, prevents pilling, and ensures softness, color retention, and is very safe for one's health.",
        img: img1,
        color: "blue",
        slug: "women-polo-shirt-01", // Add this slug for matching
        path: "/catalog/women-polo-shirt-01" // Optional if needed elsewhere
    },
    {
        title: "Modal Loose Fit Women's Polo",
        description: "The Modal Loose Fit Women's Polo uses eco-friendly fabric technology that prevents shrinkage. This modal fabric polo shirt is ideal for those working in dynamic environments today.",
        img: img2,
        color: "pink",
        slug: "women-polo-shirt-02", // Add this slug for matching
        path: "/catalog/women-polo-shirt-02"
    },
    {
        title: "Coolmax Lacoste Women's Polo",
        description: "This women's polo shirt is made from Coolmax fabric, providing a cool and refreshing feel when worn. The design of this Coolmax polo is incredibly comfortable and creates a sleek appearance, making it a hot item for the upcoming season.",
        img: img3,
        color: "orange",
        slug: "women-polo-shirt-03", // Add this slug for matching
        path: "/catalog/women-polo-shirt-03"
    }
];

// Function to get product by slug
const getProductBySlug = (slug) => {
    return heroSliderData.find(product => product.slug === slug);
};

// Function to get multiple products (e.g., for related products)
const getProducts = (count) => {
    return heroSliderData.slice(0, count); // Adjust as needed, this returns the first 'count' products
};
