import React from 'react';

import Helmet from '../components/Helmet';
import Section, { SectionBody, SectionTitle } from '../components/Section';
import Grid from '../components/Grid';
import ProductCard from '../components/ProductCard';
import ProductView from '../components/ProductView';

import productData from '../assets/fake-data/products';

const Product = (props) => {
    // Get the product slug from route params
    const productSlug = props.match.params.slug;

    // Fetch the product based on slug
    const product = productData.getProductBySlug(productSlug);

    // Get related products
    const relatedProducts = productData.getProducts(8);

    // Ensure the page scrolls to the top when the product changes
    React.useEffect(() => {
        if (product) {
            window.scrollTo(0, 0);
        }
    }, [product]);

    // Render the component
    return product ? (
        <Helmet title={product.title}>
            {/* Product Details Section */}
            <Section>
                <SectionBody>
                    <ProductView product={product} />
                </SectionBody>
            </Section>

            {/* Related Products Section */}
            <Section>
                <SectionTitle>Explore More</SectionTitle>
                <SectionBody>
                    <Grid col={4} mdCol={2} smCol={1} gap={20}>
                        {relatedProducts.map((item, index) => (
                            <ProductCard
                                key={index}
                                img01={item.image01}
                                img02={item.image02}
                                name={item.title}
                                price={Number(item.price)}
                                slug={item.slug}
                            />
                        ))}
                    </Grid>
                </SectionBody>
            </Section>
        </Helmet>
    ) : (
        // Fallback UI for when the product is not found
        <div className="product-not-found">
            <h2>Product Not Found</h2>
            <p>We couldn't find the product you're looking for. Please check the URL or explore our catalog.</p>
        </div>
    );
};

export default Product;
